ValidationRule.errorConditionFormula:
AND(
  ISNEW(),
  OR(
    ISPICKVAL(animalos__Type__c, "Puppy"),
    ISPICKVAL(animalos__Type__c, "Kitten")
  ),
  NOT(
    OR(
      ISPICKVAL(animalos__Age_Group__c, "Baby 0 - 8 wks"),
      ISPICKVAL(animalos__Age_Group__c, "Juvenile 8 wks - 6 mths")
    )
  )
)
